// Laravel: Submissions and judging model relationship
class Submission extends Model {
    public function judges() {
        return $this->belongsToMany(User::class, 'judge_scores')
                    ->withPivot('score');
    }
}

// Laravel Middleware for age-gate/access control
public function handle($request, Closure $next)
{
    if (Auth::user()->age < 13) {
        return redirect('/parental-consent');
    }
    return $next($request);
}
